import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const instructors = [
  { name: "Alex Johnson", role: "Hip Hop", image: "/images/instructor1.jpg" },
  { name: "Maria Smith", role: "Ballet", image: "/images/instructor2.jpg" },
  { name: "Daniel Lee", role: "Contemporary", image: "/images/instructor3.jpg" },
];

const Instructors = () => {
  const cardRefs = useRef([]);

  useEffect(() => {
    cardRefs.current.forEach((el, i) => {
      gsap.from(el, { opacity: 0, y: 50, duration: 1, delay: i * 0.3 });
    });
  }, []);

  return (
    <section id="instructors" className="p-16">
      <h2 className="text-4xl font-bold text-center mb-12">Meet Our Instructors</h2>
      <div className="grid md:grid-cols-3 gap-8">
        {instructors.map((inst, index) => (
          <div
            key={index}
            ref={(el) => (cardRefs.current[index] = el)}
            className="bg-white rounded-xl shadow-lg overflow-hidden hover:scale-105 transition-transform duration-300 text-center p-6"
          >
            <img src={inst.image} alt={inst.name} className="w-40 h-40 object-cover rounded-full mx-auto mb-4" />
            <h3 className="text-2xl font-bold">{inst.name}</h3>
            <p className="text-gray-600">{inst.role}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Instructors;
