import React from "react";

const classesData = [
  { title: "Hip Hop", image: "https://source.unsplash.com/400x300/?hiphop,dance" },
  { title: "Ballet", image: "https://source.unsplash.com/400x300/?ballet,dance" },
  { title: "Contemporary", image: "https://source.unsplash.com/400x300/?contemporary,dance" },
  { title: "Jazz", image: "https://source.unsplash.com/400x300/?jazz,dance" },
];

const Classes = () => {
  return (
    <section className="py-20 bg-gray-100">
      <h2 className="text-4xl text-center font-bold mb-12">Our Classes</h2>
      <div className="flex flex-wrap justify-center gap-6">
        {classesData.map((cls, idx) => (
          <div
            key={idx}
            className="w-80 bg-white shadow-lg rounded-lg overflow-hidden transform hover:scale-105 transition-transform duration-300"
          >
            <img src={cls.image} alt={cls.title} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h3 className="text-2xl font-semibold">{cls.title}</h3>
              <p className="text-gray-600 mt-2">
                Join our {cls.title} class and enhance your skills with our expert instructors.
              </p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Classes;
