import React, { useEffect, useRef } from "react";
import gsap from "gsap";

const instructors = [
  { name: "Aarti Sharma", image: "https://source.unsplash.com/400x400/?woman,dancer" },
  { name: "Rahul Verma", image: "https://source.unsplash.com/400x400/?man,dancer" },
  { name: "Maya Singh", image: "https://source.unsplash.com/400x400/?female,dancer" },
];

const Instructors = () => {
  const cardsRef = useRef([]);

  useEffect(() => {
    gsap.from(cardsRef.current, {
      opacity: 0,
      y: 50,
      stagger: 0.2,
      duration: 1,
      ease: "power3.out",
    });
  }, []);

  return (
    <section className="py-20">
      <h2 className="text-4xl text-center font-bold mb-12">Meet Our Instructors</h2>
      <div className="flex flex-wrap justify-center gap-6">
        {instructors.map((ins, idx) => (
          <div
            key={idx}
            ref={(el) => (cardsRef.current[idx] = el)}
            className="w-72 bg-white shadow-lg rounded-lg overflow-hidden hover:scale-105 transform transition-transform duration-300"
          >
            <img src={ins.image} alt={ins.name} className="w-full h-72 object-cover" />
            <div className="p-4 text-center">
              <h3 className="text-2xl font-semibold">{ins.name}</h3>
              <p className="text-gray-600 mt-1">Professional Dance Instructor</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Instructors;
