// src/Components/Hero.js
import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/autoplay";
import { Pagination, Autoplay } from "swiper/modules";

const slides = [
  {
    title: "Welcome to Dance Studio",
    subtitle: "Learn, Perform, Shine",
    image: "/images/slide1.jpg", // add images in public/images/
  },
  {
    title: "Hip Hop Classes",
    subtitle: "Groove like a pro",
    image: "/images/slide2.jpg",
  },
  {
    title: "Ballet & Contemporary",
    subtitle: "Grace in motion",
    image: "/images/slide3.jpg",
  },
];

const Hero = () => {
  return (
    <div className="w-full h-screen relative">
      <Swiper
        modules={[Pagination, Autoplay]}
        spaceBetween={30}
        slidesPerView={1}
        autoplay={{ delay: 4000 }}
        pagination={{ clickable: true }}
        loop={true}
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={index}>
            <div
              className="w-full h-screen bg-cover bg-center flex flex-col justify-center items-center text-white"
              style={{ backgroundImage: `url(${slide.image})` }}
            >
              <h1 className="text-5xl font-bold animate-fadeIn">{slide.title}</h1>
              <p className="mt-4 text-2xl animate-fadeIn delay-200">
                {slide.subtitle}
              </p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Hero;
