// React & Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/autoplay";

// Correct module imports for Swiper 10+
import { Pagination, Autoplay } from "swiper/modules";

import gsap from "gsap";


const slides = [
  { title: "Dance Studio", subtitle: "Learn, Perform, Shine", image: "https://source.unsplash.com/1600x900/?dance,studio" },
  { title: "Hip Hop & Contemporary", subtitle: "Express Your Talent", image: "https://source.unsplash.com/1600x900/?dancing" },
  { title: "Ballet Classes", subtitle: "Grace in Motion", image: "https://source.unsplash.com/1600x900/?ballet,dance" },
];

const Hero = () => {
  return (
    <section className="relative w-full h-screen">
      <Swiper
        spaceBetween={0}
        slidesPerView={1}
        autoplay={{ delay: 4000, disableOnInteraction: false }}
        loop
        pagination={{ clickable: true }}
      >
        {slides.map((slide, idx) => (
          <SwiperSlide key={idx}>
            <div
              className="w-full h-screen bg-cover bg-center flex items-center justify-center"
              style={{ backgroundImage: `url(${slide.image})` }}
            >
              <div className="text-center text-white bg-black bg-opacity-40 p-8 rounded-md animate-fadeIn">
                <h1 className="text-5xl font-bold mb-4">{slide.title}</h1>
                <p className="text-2xl">{slide.subtitle}</p>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
};

export default Hero;
