import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const Contact = () => {
  const formRef = useRef(null);

  useEffect(() => {
    gsap.from(formRef.current, { opacity: 0, y: 50, duration: 1 });
  }, []);

  return (
    <section id="contact" className="p-16">
      <h2 className="text-4xl font-bold text-center mb-12">Contact Us</h2>
      <form ref={formRef} className="max-w-2xl mx-auto flex flex-col gap-6 bg-white p-8 rounded-xl shadow-lg">
        <input type="text" placeholder="Your Name" className="p-4 border rounded-lg" />
        <input type="email" placeholder="Email Address" className="p-4 border rounded-lg" />
        <textarea placeholder="Message" className="p-4 border rounded-lg"></textarea>
        <button type="submit" className="bg-red-500 text-white py-3 rounded-lg font-bold hover:bg-red-600 transition-colors">
          Send Message
        </button>
      </form>
    </section>
  );
};

export default Contact;
