import React, { useEffect, useRef, useState } from "react";
import { gsap } from "gsap";

const Contact = () => {
  const formRef = useRef(null);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    gsap.from(formRef.current, { opacity: 1, y: 50, duration: 1 });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const response = await fetch("https://formspree.io/f/xrbrzkvb", { // <-- replace with your Formspree URL
        method: "POST",
        body: formData,
        headers: {
          Accept: "application/json",
        },
      });

      if (response.ok) {
        setSubmitted(true);
        e.target.reset();
      } else {
        alert("Oops! There was a problem submitting your form.");
      }
    } catch (error) {
      alert("Error submitting the form.");
    }
  };

  return (
    <section id="contact" className="p-16 bg-gray-100">
      <h2 className="text-4xl font-bold text-center mb-12">Contact Us</h2>

      {!submitted ? (
        <form
          ref={formRef}
          onSubmit={handleSubmit}
          className="max-w-2xl mx-auto flex flex-col gap-6 bg-white p-8 rounded-xl shadow-lg"
        >
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            className="p-4 border rounded-lg"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email Address"
            className="p-4 border rounded-lg"
            required
          />
          <textarea
            name="message"
            placeholder="Message"
            className="p-4 border rounded-lg"
            required
          ></textarea>
          <button
            type="submit"
            className="bg-red-500 text-white py-3 rounded-lg font-bold hover:bg-red-600 transition-colors"
          >
            Send Message
          </button>
        </form>
      ) : (
        <div className="text-center text-2xl text-green-600 font-semibold">
          Thank you! Your message has been sent.
        </div>
      )}
    </section>
  );
};

export default Contact;
